import { useEffect, useState } from "react";
import "./App.css";
import Item from "./components/Item/item";
import Home from "./components/home";
import MyFetch from "./components/myfetchs";
import Counter from "./components/CounterWithCustomHook";
import PlayingWithHOC from "./components/hocValidation";
import RenderPropExample from "./components/hocValidation/renderPropExample";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import InputInterchanged from "./components/InputInterchanged";
import SonOfApp from './components/ContextPoc/SonOfApp';

function App() {
  const [selected, setSelected] = useState(false);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    console.clear();

    const getMockProducts = (resolved) => {
      setTimeout(() => {
        resolved([{
          id: 1,
          name: 't-shirt',
          description: 't-shirt description',
          stock: 10,
          image: 'https://t1.gstatic.com/licensed-image?q=tbn:ANd9GcTHA8sTYngrF9FsGFcsv_vq3_ULeEG7DvrsIJLohckJnRPw4XBAx-Z9wQ6XOhMc-pzpaijFkpUWC86SKqE',
        },
        {
          id: 2,
          name: 'pants',
          description: 'pants description',
          stock: 10,
          image: "data:image/jpeg;base64,",
        },
        {
          id: 3,
          name: 'shoes',
          description: 'shoes description',
          stock: 10,
          image: 'https://t1.gstatic.com/licensed-image?q=tbn:ANd9GcTHA8sTYngrF9FsGFcsv_vq3_ULeEG7DvrsIJLohckJnRPw4XBAx-Z9wQ6XOhMc-pzpaijFkpUWC86SKqE',
        },
        ]);
      }, 3000);
    };
    const mockApiProducts = new Promise(getMockProducts)

    const updateProducts = (response) => {
      console.log(response);
      setProducts(response);
    };
    const showError = (error) => {
      console.log("Error inesperado, pruebe en unos minutos por favor");
    };
    mockApiProducts
      .then(updateProducts)
      .catch(showError)
  }, [])

  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route exact path="/" element={<Home />} />
          <Route exact path="/fetch" element={<MyFetch />} />
          <Route exact path="/fetch/:name" element={<MyFetch />} />
          <Route exact path="/counter" element={<Counter />} />
          <Route exact path="/hoc" element={<PlayingWithHOC edad={50} />} />
          <Route exact path="/renderprop" element={<RenderPropExample />} />
          <Route exact path="/events" element={<InputInterchanged />} />
          <Route exact path="/context" element={<SonOfApp />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;

