import { useState } from 'react';

const useCount = () => {
  const [counter, setCount] = useState(0);

  const increment = () => {
    setCount(counter + 1);
  };

  const decrement = () => {
    if(decrement)
    setCount(counter - 1);
  };

  const reset = () => {
    setCount(0);
  };

  return {
    counter,
    increment,
    decrement,
    reset
  };
}

export default useCount;
